Ext.define('Mvpc.view.LayerChooserContainer', {
    extend: 'Mvpc.view.ui.LayerChooserContainer',

    initComponent: function () {
        var me = this;
        me.callParent(arguments);
    },

    constructor: function () {
        this.callParent(arguments);
    },

    getValue: function () {
        return this.getComponent('layerCheckbox').getValue();
    },

    setValue: function (val) {
        this.getComponent('layerCheckbox').setValue(val);
    },
    
    addListener: function (ename, fn, scope, options) {
        var checkbox = this.getComponent('layerCheckbox');
        if (checkbox) {
            checkbox.addListener(ename, fn, scope, options);
        }
    }
});