Ext.define('Mvpd.view.DownloadTabContainer', {
    extend: 'Mvpd.view.ui.DownloadTabContainer',
    requires: [],

    initComponent: function() {
        var me = this;
        me.callParent(arguments);
    },

    constructor: function () {
        this.callParent(arguments);
        window.downloadBasketWindow = this;
        window.downloadTabContainer = this.getComponent('downloadTabContainer');
        window.downloadBasketWindow.internalTab = window.downloadTabContainer.getComponent('internalTab');
        window.downloadBasketWindow.sidTab = window.downloadTabContainer.getComponent('sidTab');
        this.internalTab.add(window.internalContainer = Ext.create('Ext.container.Container', {
            itemId: 'internalContainer',
            layout: 'fit'
        }));
        this.sidTab.add(window.externalContainer = Ext.create('Ext.container.Container', {
            itemId: 'externalContainer',
            layout: 'fit'
        }));
        if (!AppConfig.isDevelopment) window.downloadBasketWindow.sidTab.disable();
    },

    statics: {
        show: function () {
            window.downloadBasketWindow.show();
        },

        addDads: function (view) {
            window.externalContainer.removeAll();
            window.externalContainer.add(view);
            window.downloadBasketWindow.show();
            window.downloadBasketWindow.sidTab.enable();
            window.downloadTabContainer.setActiveTab(1);

        },

        remove: function () {

        },

        addInternal: function (view) {
            window.internalContainer.removeAll();
            window.internalContainer.add(view);
            window.downloadBasketWindow.show();
            window.downloadTabContainer.setActiveTab(0);
        }
    },


});